import {
  IonHeader,
  IonToolbar,
  IonMenuToggle,
  IonButton,
  IonButtons,
  IonIcon,
} from "@ionic/vue";
import { menu as MenuIcon, arrowBack } from "ionicons/icons";
import { useRoute, useRouter } from "vue-router";

defineProps({
  menu: {
    type: Boolean,
    default: false,
  },
  hasClose: {
    type: Boolean,
    default: false,
  },
  canGoBack: {
    type: Boolean,
    default: false,
  },
});

const route = useRoute();
const router = useRouter();

const goBack = () => {
  if (route.path === "/") {
    return;
  } else {
    window.history.back();
  }
};

const close = () => {
  router.push("/");
};
